INSERT INTO ListeMedicamentsPostATU (IdMedicamentATU, PostATUTermine, UCD7ATUN, UCD7ATUC, DenominationCommune, StatutAnterieur, PriseEnChargePostATU, InitierNouveauxTraitements, Observations, InscriptionRemboursement, IndicationEvaluationDefavorable, ExtensionIndicationEnCours, ConclusionAvisHAS, DateOctroiAMM, DateDebutPostATU, DateFinPostATU) VALUES ('482', 'O', '9416765', '9425184', 'inotuzumab ozogamicine', 'ATUC', 'N', 'O', 'LE DISPOSITIF POST ATU EST ARRETE.', 'O', 'NON', 'NON', 'L’indication de la spécialité BESPONSA (inotozumab ozogamicin), validée par le CHMP, qui n’a pas fait l’objet d’une autorisation temporaire d’utilisation correspond au « traitement de la leucémie aiguë lymphoblastique (LAL) à précurseur B, CD22+, chez l’adulte en rechute ou réfractaire et inéligible à une allogreffe de cellules souches. »Dans cette indication, la Haute Autorité de santé a identifié des alternatives thérapeutiques prises en charge par les régimes obligatoires de sécurité sociale. Ces alternatives sont mentionnées en annexe de l''avis n° 2017.0063/AC/SEM du 21 juin 2017 du collège de la Haute Autorité de santé,', '29/06/2017 00:00:00', '29/09/2017 00:00:00', '16/03/2018 00:00:00')
INSERT INTO ListeMedicamentsPostATU (IdMedicamentATU, PostATUTermine, UCD7ATUN, UCD7ATUC, DenominationCommune, StatutAnterieur, PriseEnChargePostATU, InitierNouveauxTraitements, Observations, InscriptionRemboursement, IndicationEvaluationDefavorable, ExtensionIndicationEnCours, ConclusionAvisHAS, DateOctroiAMM, DateDebutPostATU, DateFinPostATU) VALUES ('483', 'O', '0', '9405997ATU nominatives9412129ATU de cohorte', 'blinatumomab', 'ATUC', 'N', 'O', 'LE DISPOSITIF POST ATU EST ARRETE.', 'O', 'NON', 'OUI Traitement des patients adultes présentant une leucémie aiguë lymphoblastique (LAL) à précurseurs B avec chromosome Philadelphie négatif en rechute ou réfractaire.', 'L’indication validée par le CHMP chez les patients ayant une LAL en rechute ou réfractaire est identique à l’une des indications définie dans l’ATU de cohorte de la spécialité BLINCYTO (AVIS N° 2015.0101/AC/SEM du 18 novembre 2015 du collège de la Haute Autorité de Santé),Par conséquent, cette indication n’entre pas dans le cadre du 2° du I de l’article L. 162-16-5-2 du code de lasécurité sociale qui concerne uniquement les indications mentionnées dans l’AMM et n’ayant pas fait l’objetd’une ATU. Il n’y a donc pas lieu d’identifier, pour cette spécialité, des alternatives thérapeutiques prises en charge par les régimes obligatoires de sécurité sociale.L’indication définie dans l’ATU de cohorte qui correspond à une autre population, celle des patients ayant une LAL à précurseurs B en rémission hématologique complète et avec présence d’une maladie résiduelle minimale (MRD), n’a pas fait l’objet d’une demande d’AMM à ce jour. Par conséquent, elle n’entre pas dans le cadre du 2° du I de l’article L. 162-16-5-2 du code de la sécurité sociale. Il n’y a donc pas lieu d’identifier des alternatives thérapeutiques prises en charge par les régimes obligatoires de sécurité sociale dans cette indication.', '23/11/2015 00:00:00', '01/03/2016 00:00:00', '06/05/2017 00:00:00')
INSERT INTO ListeMedicamentsPostATU (IdMedicamentATU, PostATUTermine, UCD7ATUN, UCD7ATUC, DenominationCommune, StatutAnterieur, PriseEnChargePostATU, InitierNouveauxTraitements, Observations, InscriptionRemboursement, IndicationEvaluationDefavorable, ExtensionIndicationEnCours, ConclusionAvisHAS, DateOctroiAMM, DateDebutPostATU, DateFinPostATU) VALUES ('484', 'O', '0', '9420005', 'cabozantinib', 'ATUC', 'N', 'O', 'LE DISPOSITIF POST ATU EST ARRETE.', 'O', 'NON', 'NON', 'L’indication de l’autorisation de mise sur le marché des spécialités CABOMETYX 20 mg, 40 mg et 60 mg (cabozantinib) qui n’a pas fait l’objet d’une autorisation temporaire d’utilisation de cohorte est le traitement de 2ème ligne du carcinome à cellules rénales (CCR) au stade avancé.Dans cette indication, la Haute Autorité de santé a identifié des alternatives thérapeutiques prises en charge par les régimes obligatoires de sécurité sociale. Ces alternatives sont mentionnées en annexe de l''avis n°2016.0057/AC/SEM du 7 septembre 2016 du collège de la Haute Autorité de santé .Cette indication ne bénéficie donc pas du dispositif post-ATU.', '09/09/2016 00:00:00', '09/12/2016 00:00:00', '16/02/2018 00:00:00')
