INSERT INTO ListeMedicamentsPostATU (IdMedicamentATU, PostATUTermine, UCD7ATUN, UCD7ATUC, DenominationCommune, StatutAnterieur, PriseEnChargePostATU, InitierNouveauxTraitements, Observations, InscriptionRemboursement, IndicationEvaluationDefavorable, ExtensionIndicationEnCours, ConclusionAvisHAS, DateOctroiAMM, DateDebutPostATU, DateFinPostATU) VALUES ('496', 'O', '0', '9408843', 'sacubitril/valsartan', 'ATUC', 'N', 'O', 'LE DISPOSITIF POST ATU EST ARRETE.', 'O', 'NON', 'NON', 'Les indications validées par le CHMP n’ayant pas fait l’objet d’une autorisation temporaire d’utilisation de cohorte correspond aux patients avec insuffisance cardiaque :- dysfonction systolique ventriculaire gauche (fraction d''éjection ventriculaire gauche ≤ 40 %) symptomatique de classe fonctionnelle NYHA II ayant présenté moins de 2 hospitalisations pour décompensation cardiaque dans l’année documentée ou n’ayant pas utilisé de diurétiques IV.- dysfonction systolique ventriculaire gauche (fraction d''éjection ventriculaire gauche comprise entre 40 et 50%) symptomatique.Dans ces indications validées par le CHMP qui n’ont pas fait l’objet d’une autorisation temporaire d’utilisation, la HAS a identifié des thérapeutiques prises en charge par les régimes obligatoires de sécurité sociale. Ces alternatives sont mentionnées en annexe de l''avis N° 2015.0095/AC/SEM du 4 novembre 2015 du collège de la Haute Autorité de Santé.', '19/11/2015 00:00:00', '04/01/2016 00:00:00', '15/10/2018 00:00:00')
