INSERT INTO ListeMedicamentsPostATU (IdMedicamentATU, PostATUTermine, UCD7ATUN, UCD7ATUC, DenominationCommune, StatutAnterieur, PriseEnChargePostATU, InitierNouveauxTraitements, Observations, InscriptionRemboursement, IndicationEvaluationDefavorable, ExtensionIndicationEnCours, ConclusionAvisHAS, DateOctroiAMM, DateDebutPostATU, DateFinPostATU) VALUES ('504', 'O', '0', '9404957', 'lédipasvir/sofosbuvir', 'ATUC', 'N', 'O', 'LE DISPOSITIF POST ATU EST ARRETE.', 'O', 'NON', 'NON', 'L’indication de l’autorisation de mise sur le marché des spécialités HARVONI qui n’a pas fait l’objet d’une autorisation temporaire d’utilisation de cohorte concerne les patients adultes ayant une hépatite C chronique due au virus de génotype 1,3 ou 4, avec une maladie à un stade moins avancé (avec fibrose hépatique < ou = F2 et ne présentant pas des manifestations extra-hépatiques du VHC) et,                                                                                                                                                                                                                                    - n''ayant pas subi de transplantation hépatique,                                                                                                                                                                                                                                                                      - n''ayant pas subi une transplantation rénale.Pour ces indications, la HAS a identifié des alternatives thérapeutiques prises en charge par les régimes obligatoires de la sécurité sociale ; ces alternatives sont mentionnées en annexe de l''avis de la HAS (Avis n°2014.0115/SEM du 17 décembre 2014).', '17/11/2014 00:00:00', '18/12/2014 00:00:00', '18/06/2015 00:00:00')
INSERT INTO ListeMedicamentsPostATU (IdMedicamentATU, PostATUTermine, UCD7ATUN, UCD7ATUC, DenominationCommune, StatutAnterieur, PriseEnChargePostATU, InitierNouveauxTraitements, Observations, InscriptionRemboursement, IndicationEvaluationDefavorable, ExtensionIndicationEnCours, ConclusionAvisHAS, DateOctroiAMM, DateDebutPostATU, DateFinPostATU) VALUES ('505', 'O', '0', '9353869', 'propranolol', 'ATUC', 'N', 'O', 'LE DISPOSITIF POST ATU EST ARRETE.', 'O', 'NON', 'NON', 'Les indications de l’autorisation de mise sur le marché de la spécialité HEMANGIOL qui n’ont pas fait l’objet d’une autorisation temporaire d’utilisation de cohorte concernent les patients avec une hémangiome :- ulcérés avec douleurs,- avec risque de cicatrice permanente ou défigurante.Pour ces indications, la HAS a identifié des alternatives thérapeutiques prises en charge par les régimes obligatoires de la sécurité sociale ; ces alternatives sont mentionnées en annexe de l''avis n° 2014.0019/AC/SEM du 2 avril 2014 du collège de la HAS.', '23/04/2014 00:00:00', '28/07/2014 00:00:00', '21/10/2014 00:00:00')
INSERT INTO ListeMedicamentsPostATU (IdMedicamentATU, PostATUTermine, UCD7ATUN, UCD7ATUC, DenominationCommune, StatutAnterieur, PriseEnChargePostATU, InitierNouveauxTraitements, Observations, InscriptionRemboursement, IndicationEvaluationDefavorable, ExtensionIndicationEnCours, ConclusionAvisHAS, DateOctroiAMM, DateDebutPostATU, DateFinPostATU) VALUES ('506', 'O', '0', '9407677', 'palbociclib', 'ATUC', 'N', 'O', 'LE DISPOSITIF POST ATU EST ARRETE.', 'O', 'NON', 'NON', 'Les indications de l’autorisation de mise sur le marché de la spécialité IBRANCE (palbociclib) qui n’ont pas fait l’objet d’une autorisation temporaire d’utilisation de cohorte sont :- le traitement hormonal de première ligne du cancer du sein localement avancé/métastatique, positif aux récepteurs hormonaux (RH) et négatif au récepteur du facteur de croissance épidermique humain chez la femme ménopausée ou non ménopausée,- le traitement du cancer du sein localement avancé/métastatique, positif aux récepteurs hormonaux (RH) et négatif au récepteur du facteur de croissance épidermique humain avec atteinte viscérale quel que soit le statut de la patiente : ménopausée ou non ménopausée,- le traitement hormonal de seconde ligne ou plus du cancer du sein localement avancé/métastatique, positif aux récepteurs hormonaux (RH) et négatif au récepteur du facteur de croissance épidermique humain chez la femme ménopausée ou non ménopausée.La Haute Autorité de santé a identifié des alternatives thérapeutiques prises en charge par les régimes obligatoires de sécurité sociale dans les indications précitées, à l’exception de l’indication du traitement hormonal de seconde ligne ou plus du cancer du sein chez les patientes non ménopausées. Ces alternatives thérapeutiques sont mentionnées en annexe de l''avis n°2016.0063/AC/SEM du 26 octobre 2016 du collège de la Haute Autorité de santé .Ces indications ne sont donc pas prises en charge au titre du post-ATU, à l''exception de l’indication du traitement hormonal de seconde ligne ou plus du cancer du sein chez les patientes non ménopausées.', '09/11/2016 00:00:00', '16/01/2017 00:00:00', '24/03/2018 00:00:00')
