INSERT INTO ListeMedicamentsPostATU (IdMedicamentATU, PostATUTermine, UCD7ATUN, UCD7ATUC, DenominationCommune, StatutAnterieur, PriseEnChargePostATU, InitierNouveauxTraitements, Observations, InscriptionRemboursement, IndicationEvaluationDefavorable, ExtensionIndicationEnCours, ConclusionAvisHAS, DateOctroiAMM, DateDebutPostATU, DateFinPostATU) VALUES ('510', 'O', '0', '9402036', 'kétoconazole', 'ATUC', 'N', 'O', 'LE DISPOSITIF POST ATU EST ARRETE.', 'O', 'NON', 'NON', 'Dans le traitement du syndrome de Cushing, l’indication de l’autorisation de mise sur le marché de la spécialité KETOCONAZOLE HRA qui n’a pas fait l’objet d’une autorisation temporaire d’utilisation de cohorte concerne les patients avec syndrome de Cushing pour lesquels il existe des alternatives médicamenteuses appropriées.La HAS a identifié des thérapeutiques prises en charge par les régimes obligatoires de sécurité sociale, dans certaines manifestations du syndrome de Cushing, qui sont :- La maladie de Cushing en cas de syndrome de Cushing-ACTH dépendant avec une ACTH d’origine eutopique sécrétée par une tumeur bénigne hypophysaire,- Le carcinome corticosurrénalien évolué,- Les hypercorticismes indépendants de l''ACTH (syndrome de Cushing tumoral).Ces alternatives sont mentionnées en annexe de l''avis N° 2014.0110/AC/SEM du 12 novembre 2014 du collège de la Haute Autorité de Santé.', '19/11/2014 00:00:00', '06/03/2015 00:00:00', '11/03/2016 00:00:00')
